var dsBtreport = view.get("#dsBtreport");
var selectPerson = dgShareUser.get("selection");
var cur = dsBtreport.getData('#');
var ajaxShare = view.id('ajaxActionShareBtreport');
var selectDepart = view.get("#dtShareDepart").getCheckedNodes();
var persons = new Array();
var departments = new Array();
var i = 0;
if(selectPerson && selectPerson.length){
	
	selectPerson.each(function(per){
		persons[i] = per.get("username");
		i++;
	});	
}
if(selectDepart && selectDepart.length){
	i = 0;
	selectDepart.each(function(dep){
		departments[i] = dep.get('data.name');
		i++;
	});	
}
ajaxShare.set('parameter',{
	'btreport':cur,
	'selectPerson':	persons,
	'selectDepart': departments
});
ajaxShare.execute();
cur.set("status",3);
view.id('uaBtreport').execute();
view.id('diaShareBtreport').hide();

