ToolBarButton.ClientEvent:
var uploadAction = view.get('#batchUpload');
var dataset = view.get('#dataSetFiles');
var selections = view.get('#dataGridFiles.selection');

if(!selections){
	return;
}

if(!selections.length){
	selections = [selections];
}

var selectionCopy = [];
for (var i = 0; i < selections.length; i++) {
	selectionCopy.push(selections[i]);
}

for(var i=0;i<selectionCopy.length;i++){
	var entity = selectionCopy[i];
	uploadAction.removeFile(entity.get('id'));
	entity.remove();
}

view.get('#batchUpload').start();

	/*
 	 * 对用户新增操作进行记录，在用户操作日志表中新增一条记录。
 	 */
	var myDate = new Date();
	view.id('dsUserOperationLog').getData().insert({
		logOperationType:"1",
		operationPerson: "${loginUsername}",
		operationContent:"对出差报告管理表子表附件文档表新增选定记录",
		operationTime:myDate
	});
	view.id('uaUserOperationLog').execute();

DataGrid.ClientEvent:
var ProgressCellRenderer = $extend(dorado.widget.grid.SubControlCellRenderer, {
    createSubControl: function(arg) {
        return new dorado.widget.ProgressBar({
            value:arg.data.get("percent")
        });
    },

    refreshSubControl: function(progressBar, arg) {
		progressBar.set("value",arg.data.get("percent"));
        progressBar.refresh();
    }
});

self.set("&percent.renderer",new ProgressCellRenderer());

UploadAction.ClientEvent:
var error = arg.error;
dorado.MessageBox.show({
	title:error.message,
	message:error.file.name + " 超过大小限制:"+ self.get('maxFileSize'),
	icon: "ERROR",
	buttons: ["ok"]
});

var file = arg.file;//file：完成上传的文件
var entity = view.get('#dataSetFiles').getData("[@.get('id')=='"+file.id+"']");
entity.set('percent',100);
entity.set('status',file.status);


