ToolBarButton.ClientEvent:
	view.id('dsResourcedownload').getData().insert();
	var cur=view.id('dsResourcedownload').getData('#');
	var guid = "";
	for(var i=1;i<=32;i++){
		var n = Math.floor(Math.random()*16.0).toString(16);
		guid += n;
		if((i==8)||(i==12)||(i==16)||(i==20))
		   guid += "-";
	}
	cur.set("oid",guid);
	view.id('diaResourcedownload').show();
	
/*
 * 对用户新增操作进行记录，在用户操作日志表中新增一条记录。
 */
	var myDate = new Date();
	view.id('dsUserOperationLog').getData().insert({
		logOperationType:"0",
		operationPerson: "${loginUsername}",
		operationContent:"对常用资源下载表新增一条记录",
		operationTime:myDate
	});
	view.id('uaUserOperationLog').execute();

	//修改设置链接
var cur=view.id('dsResourcedownload').getData('#');
	if (cur) {
		view.id('diaResourcedownload').show();
	}
   var file = cur.get("efile");
   var link = view.id("lefile");
   if(file){
   var oid = cur.get("oid");
   var href = "/escmp/upload/Comm_Resourcedownload/" + oid + "/" + file;
   link.set({
	href: href,
	visible: true
   });
 }
   else{
   link.set("visible",false);
 }
 
/*
 * 对用户修改操作进行记录，在用户操作日志表中新增一条记录。
 */
var myDate = new Date();
view.id('dsUserOperationLog').getData().insert({
	logOperationType:"1",
	operationPerson: "${loginUsername}",
	operationContent:"对常用资源下载表修改选定记录",
	operationTime:myDate
});
view.id('uaUserOperationLog').execute();

var cur=view.id('dsResourcedownload').getData('#');
	var ua=view.id('uaResourcedownload')
	if(cur){
		dorado.MessageBox.confirm('您是否确认删除此条记录?', function(){
			
			/*
 			 * 对用户删除操作进行记录，在用户操作日志表中新增一条记录。
 			 */
			var myDate = new Date();
			view.id('dsUserOperationLog').getData().insert({
				logOperationType:"2",
				operationPerson: "${loginUsername}",
				operationContent:"对常用资源下载表删除所选记录",
				operationTime:myDate
			});
			view.id('uaUserOperationLog').execute();
			
			cur.remove();
			ua.execute();
				}); 
			}

DataColumn.ClientEvent:
jQuery(arg.dom).empty();
var file = arg.data.get("efile");
var oid = arg.data.get("oid");
var href = "/escmp/upload/Comm_Resourcedownload/" + oid + "/" + file;
jQuery(arg.dom).xCreate({
	tagName:"A",
	href:href,
	content:arg.data.get("efile")
});

Button.ClientEvent:
var cur=view.id('dsResourcedownload').getData('#');
cur.cancel();
view.id('diaResourcedownload').hide();
/*
 * 删除当前用户操作日志记录并保存
 */
view.id('dsUserOperationLog').getData().remove()
view.id('uaUserOperationLog').execute();

UploadAction.ClientEvent:
var cur=view.id('dsResourcedownload').getData('#');
var value = "/Comm_Resourcedownload/" + cur.get("oid") + "/";
self.set("multipartParams", {record: value});

var returnValue = arg.file.name;
var cur=view.id('dsResourcedownload').getData('#');
cur.set("efile",returnValue);
var link = view.id("lefile");
if(returnValue){
	
	var oid = cur.get("oid");
    var href = "/escmp/upload/Comm_Resourcedownload/" + oid + "/" + returnValue;
    link.set({
		href: href,
		visible: true
	});
	
}
else{
    link.set("visible",false);
}

var error = arg.error;
dorado.MessageBox.show({
	title:error.message,
	message:error.file.name + " 超过大小限制:"+ self.get('maxFileSize'),
	icon: "WARNING",
	buttons: ["ok"]
});

AjaxAction.ClientEvent:
var cur=view.id('dsResourcedownload').getData('#');
var value = "/Comm_Resourcedownload/" + cur.get("oid") + "/";
var file = cur.get("efile");
self.set("parameter",{
	record: value,
	filename: file
});
cur.set("efile",null);
var link = view.id("lefile");
link.set("visible",false);
