DataSetDropDown.ClientEvent:
var dsTasklist = this.get("#dsFlightrestrict");
dsTasklist.set("parameter", {
	aircrafttype: arg.selectedValue
});
dsTasklist.flushAsync();

DataTree.ClientEvent:
var dropDown = dorado.widget.DropDown.findDropDown(self);
var dataTree1 = this.get("#dTFtypes1");
var NodeType = dataTree1.get("currentNode.bindingConfig.name");
var info = this.get("#info");
if(NodeType=="label"){
	var label = dataTree1.get("currentNode.data").get("label");
	dropDown.close(label);
	view.id('dsFlightrestrict').set("parameter",{
	});
	view.id('dsFlightrestrict').flushAsync();
	info.set("value",label);
}
if(NodeType=="name"){
	var ftypename = dataTree1.get("currentNode.data").get("ftypename");
	dropDown.close(ftypename);
	view.id('dsFlightrestrict').set("parameter",{
		ftypename:ftypename
	});
	view.id('dsFlightrestrict').flushAsync();
	info.set("value",ftypename);
}
if(NodeType=="pname"){
	var piciname = dataTree1.get("currentNode.data").get("piciName");
	dropDown.close(piciname);
	view.id('dsFlightrestrict').set("parameter",{
			piciID: dataTree1.get("currentNode.data").get("piciID")
	});
	view.id('dsFlightrestrict').flushAsync();
	info.set("value",piciname);
}
if(NodeType=="flight"){
	var outfactoryno = dataTree1.get("currentNode.data").get("outfactoryno");
	dropDown.close(outfactoryno);
	view.id('dsFlightrestrict').set("parameter",{
			outfactoryno:outfactoryno
		});
	view.id('dsFlightrestrict').flushAsync();
	info.set("value",outfactoryno);
}

AjaxAction.ClientEvent:
var cur=view.id('dsFlightrestrict').getData('#');
var value = "/Fly_Flightrestrict/" + cur.get("id") + "/";
var file = cur.get("filename");
self.set("parameter",{
	record: value,
	filename: file
});
cur.set("filename",null);
var link = view.id("lefile");
link.set("visible",false);

UploadAction.ClientEvent:
var cur=view.id('dsFlightrestrict').getData('#');
var value = "/Fly_Flightrestrict/" + cur.get("id") + "/";
self.set("multipartParams", {record: value});

var returnValue = arg.file.name;
var cur=view.id('dsFlightrestrict').getData('#');
cur.set("filename",returnValue);
var link = view.id("lefile");
if(returnValue){
	var oid = cur.get("id");
    var href = "ServletDownload?filename=Fly_Flightrestrict/" + oid + "/" + returnValue;
    link.set({
		href: href,
		visible: true
	});
}
else{
    link.set("visible",false);
}

var error = arg.error;
dorado.MessageBox.show({
	title:error.message,
	message:error.file.name + " 超过大小限制:"+ self.get('maxFileSize'),
	icon: "WARNING",
	buttons: ["ok"]
});

Dialog.ClientEvent:
var cur=view.id('dsFlightrestrict').getData('#');
if(cur){
	cur.cancel();
	view.id('diaFlightrestrict').hide();
}
var nodes=view.get("#dTFtypes").getCheckedNodes();
nodes.each(function(node){
	node.set("checked",false);
});

Button.ClientEvent:
var cur = view.id("dsFlightrestrict").getData("#");
var selections = view.get("#dTFtypes").getCheckedNodes();
var tmpList = selections.slice(0);
if(tmpList.length != 0){
	selections.each(function(entity){
		if(entity.get("bindingConfig.name") == "name")
			view.id("dsFlightrestrict").getData("#.fighterxzh").insert({
				flightRestrict: {id: cur.get("id")},
				referenceName: entity.get("data.ftypename")
			});
		if(entity.get("bindingConfig.name") == "pname")
			view.id("dsFlightrestrict").getData("#.fighterxzh").insert({
				flightRestrict: {id: cur.get("id")},
				referenceName: entity.get("data.piciID")
			});
		if(entity.get("bindingConfig.name") == "flight")
			view.id("dsFlightrestrict").getData("#.fighterxzh").insert({
				flightRestrict: {id: cur.get("id")},
				referenceName: entity.get("data.outfactoryno")
			});
	});
}
view.id('uaFlightrestrict').execute(function(){
    view.id('diaFlightrestrict').hide();
});

var cur=view.id('dsFlightrestrict').getData('#');
if(cur){
	cur.cancel();
	view.id('diaFlightrestrict').hide();
}
var nodes=view.get("#dTFtypes").getCheckedNodes();
nodes.each(function(node){
	node.set("checked",false);
});

DataTree.ClientEvent:
var Root = view.get("@Root");
var ProductTree = view.get("@Ftypes");
var Equipment = view.get("@Fpici");
var FLIGHT = view.get("@Fighterinfo");
dorado.DataPath.registerInterceptor("CURRENT_ROOT", function(data) {
		var entity = self.get("currentNode.data");
		return (entity && entity.dataType == Root) ? entity : null;
	}, function(dataType) {
		return Root;
	});
dorado.DataPath.registerInterceptor("CURRENT_TYPE", function(data) {
		var entity = self.get("currentNode.data");
		return (entity && entity.dataType == ProductTree) ? entity : null;
	}, function(dataType) {
		return ProductTree;
	});
	dorado.DataPath.registerInterceptor("CURRENT_PICI", function(data) {
		var entity = self.get("currentNode.data");
		return (entity && entity.dataType == Equipment) ? entity : null;
	}, function(dataType) {
		return Equipment;
	});
dorado.DataPath.registerInterceptor("CURRENT_FLIGHT", function(data) {
		var entity = self.get("currentNode.data");
		return (entity && entity.dataType == FLIGHT) ? entity : null;
	}, function(dataType) {
		return FLIGHT;
	});

var fighterxzh = view.id('dsFlightrestrict').getData('#.fighterxzh');
function select(nodes) {
    nodes.each(function(node) {
        select(node.get("nodes"));
        if(node.get("bindingConfig.name") == "flight"){
			fighterxzh.each(function(entity){
				if(entity.get("referenceName") == node.get("data.outfactoryno"))
					node.set("checked", true);
			});
		}
		if(node.get("bindingConfig.name") == "pname"){
			fighterxzh.each(function(entity){
				if(entity.get("referenceName") == node.get("data.piciID"))
					node.set("checked",true);
			});
		}
		if(node.get("bindingConfig.name") == "name"){
			fighterxzh.each(function(entity){
				if(entity.get("referenceName") == node.get("data.ftypename"))
					node.set("checked",true);
			});
		}
    });
}
select(this.get("#dTFtypes.nodes"));

ToolBarButton.ClientEvent:
var nodes=view.get("#dTFtypes").getCheckedNodes();
nodes.each(function(node){
	node.set("checked",false);
});
view.id("tab").set("currentTab", 0);
var guid = "";
for(var i=1;i<=32;i++){
	var n = Math.floor(Math.random()*16.0).toString(16);
	guid += n;
	if((i==8)||(i==12)||(i==16)||(i==20))
		  guid += "-";
}
dialog = view.id('diaFlightrestrict');
dialog.set("caption","新增使用限制");
view.id('dsFlightrestrict').getData().insert({
	id: guid
});
var link = view.id("lefile");
link.set("visible",false);
view.id('diaFlightrestrict').show();

dialog = view.id('diaFlightrestrict');
dialog.set("caption","修改使用限制");
var cur = view.id('dsFlightrestrict').getData('#');
if (cur) {
	view.id("tab").set("currentTab", 0);
	view.id('diaFlightrestrict').show();
	var file = cur.get("filename");
    var link = view.id("lefile");
    if(file){
        var oid = cur.get("id");
		var href = "ServletDownload?filename=Fly_Flightrestrict/" + oid + "/" + file;
        link.set({
	       href: href,
	       visible: true
        });
    }
    else
      link.set("visible",false);
}
view.id("dsFlightrestrict").getData("#").reset("fighterxzh");

var cur = view.id('dsFlightrestrict').getData('#');
var ua=view.id('uaFlightrestrict');
if(cur){
	dorado.MessageBox.confirm('您是否确认删除此条记录?', function(){
		cur.remove();
		ua.execute();
	}); 
}

var fileURL="upload/Fly_Flightrestrict/"+view.id('dsFlightrestrict').getData("#").get("id")+"/"+view.id('dsFlightrestrict').getData("#").get("filename");
var url = encodeURI(fileURL);
var path="a.jsp?url='"+ url +"'";
open(path,"_blank");

