PropertyDef.ClientEvent:
var name = arg.entity.get("name");
if(name != "总计"){
	var entitylist = [];
	var dictype = arg.entity.get("dictype");
	if(dictype == "Dic_DealStatus")
        entitylist = view.id("dsSffault").queryData("[@.get('status')=='" + name + "']");
	else if(dictype == "Dic_Gzfs")
	    entitylist = view.id("dsSffault").queryData(".gzfs");
	else if(dictype == "Dic_DutyType")
	    entitylist = view.id("dsSffault").queryData(".type");
	else if(dictype == "Dic_Department")
	    entitylist = view.id("dsSffault").queryData("[@.get('bumen')=='" + name + "'||@.get('relatebumen')=='" + name + "']");
	else if(dictype == "Dic_MajorSystem")
	    entitylist = view.id("dsSffault").queryData("[@.get('majorsystem')=='" + name + "'||@.get('relatemajor')=='" + name + "']");
	var count = 0;
    entitylist.each(function(entity){
		if(dictype == "Dic_DutyType" || dictype == "Dic_Gzfs"){
		    var has = entity.indexOf(name);
			if(has == -1)
			    return;
	    }
        count++;//JSON只有这样才能拿到个数
    });
    arg.value = count;
}

DataSetDropDown.ClientEvent:
var dsSffault = this.get("#dsSffault");
dsSffault.set("parameter", {
	ftype: arg.selectedValue
});
dsSffault.flushAsync();
