view.id("dsDutyType").flushAsync();
view.id("dsDepartment").flushAsync();
view.id("dsMajorSystem").flushAsync();

Dialog.ClientEvent:
view.id("dsDictionaryGzfs").flushAsync(function(entitylist){
	var gzfs = view.id("dsSffault").getData("#").get("gzfs");
	view.id("containerGzfs").removeAllChildren();//先清空组件
	entitylist.each(function(entity){//遍历数据字典
		var name = entity.get("name");
		var box = new dorado.widget.CheckBox();
		box.set("caption",name);//设置显示标题
		if(gzfs!=null&&gzfs.indexOf(name)!=-1)//包含的选项自动打钩
            box.set("checked",true);
        view.id("containerGzfs").addChild(box);//添加显示组件
	});
});
