view.id("dsDictionaryFoundOpp").flushAsync(function(entitylist){
	var foundopp = view.id("dsSffault").getData("#").get("foundopp");
	view.id("containerFoundopp").removeAllChildren();//先清空组件
	var checkbox = new dorado.widget.CheckBox();
	if(foundopp != null && foundopp.indexOf("飞行中") != -1)//包含的选项自动打钩
		checkbox.set("checked",true);
	checkbox.set({
		id:"checkbox",
		caption:"飞行中",
		onValueChange:function(){
			var value = checkbox.get("value");
			if(value){
				view.id("diaSfstatistic").show();
			}else{
				var dsSffault = view.id("dsSffault");
				dsSffault.getData("#").set("fdata",null);
			}
		}
	});
	view.id("containerFoundopp").addChild(checkbox);
	entitylist.each(function(entity){//遍历数据字典
		var name = entity.get("name");
		var box = new dorado.widget.CheckBox();
		box.set("caption",name);//设置显示标题
		if(foundopp!=null&&foundopp.indexOf(name)!=-1)//包含的选项自动打钩
            box.set("checked",true);
        view.id("containerFoundopp").addChild(box);//添加显示组件
	});
});
