ControlTab.ClientEvent:
var cur = view.id('dsSffault').getData("#").get("foundopp");
if(cur != null && cur.indexOf("飞行中") != -1){
	view.id("DataGridSfstatistic").set("visible",true);
}else{
	view.id("DataGridSfstatistic").set("visible",false);
}
var cur = this.get("#dsSffault.data:#");
var Sfstatistic = this.get('#dsSfstatistic');
Sfstatistic.set("parameter",{
	id: cur.get("fdata"),
	ftype: cur.get("ftype")}
);
Sfstatistic.flushAsync();

