cur1.set("fdata",cur.get("id"));
view.id('diaSfstatistic').hide();

ToolBarButton.ClientEvent:
var ftype = view.id("formElement1").get("value");
if(ftype){
	dialog = view.id('diaSffault');
	dialog.set("caption","新增问题跟踪");
	view.id('dsSffault').getData().insert({
		ftype: ftype
	});
	view.id('diaSffault').show();
	view.id("tabControl1").set("currentIndex",1);
	view.id("tab").set("currentIndex",0);
	var dsSfstatistic = this.get("#dsSfstatistic");
	dsSfstatistic.set("parameter", {
		ftype: ftype
	});
	dsSfstatistic.flushAsync();
}else{
	dorado.MessageBox.alert('请选择机型！');
}

if(view.id("tabControl1").get("currentIndex") == 1){
	var cur=view.id('dsSffault').getData('#');
	if (cur) {
		dialog = view.id('diaSffault');
	dialog.set("caption","修改问题跟踪");
	view.id('diaSffault').show();
	view.id("tab").set("currentIndex",0);
	var dsSfstatistic = this.get("#dsSfstatistic");
	dsSfstatistic.set("parameter", {
		ftype: ftype
	});
	dsSfstatistic.flushAsync();
	}
}else{
	dorado.MessageBox.alert("请先切换至“问题跟踪管理”页面！");
}

if(view.id("tabControl1").get("currentIndex") == 1){
    var cur=view.id('dsSffault').getData('#');
	var ua=view.id('uaSffault');
	if(cur){
		dorado.MessageBox.confirm('您是否确认删除此条记录?', function(){
			cur.remove();
			ua.execute();
		}); 
	} 
}else{
	dorado.MessageBox.alert("请先切换至“问题跟踪管理”页面！");
}

var dsSffault = this.get("#dsSffault");
var ftype = view.id("formElement1").get("value");
dsSffault.set("parameter", {
	ftype: ftype
});
dsSffault.flushAsync();

DataColumn.ClientEvent:
jQuery(arg.dom).empty();
jQuery(arg.dom).xCreate({
	tagName:"A",
	href:"#",
	content:"查看",
	onclick:function(){
		view.id("tabControl1").set("currentIndex",1);
		var name = view.id("dsGzfs").getData("#").get("name");
		var ftype = view.id("formElement1").get("value");
		var dsSffault = view.id("dsSffault");
        dsSffault.set("parameter", {
	        ftype: ftype,
			gzfs:name == "总计"?null:name
        });
        dsSffault.flushAsync();
	}
});

