PropertyDef.ClientEvent:
var name = arg.entity.get("name");
if(name != "总计"){
	var entitylist = [];
	var dictype = arg.entity.get("dictype");
	if(dictype == "Dic_DealStatus")
        entitylist = view.id("dsSffault").queryData("[@.get('status')=='" + name + "']");
	else if(dictype == "Dic_Gzfs")
	    entitylist = view.id("dsSffault").queryData(".gzfs");
	else if(dictype == "Dic_DutyType")
	    entitylist = view.id("dsSffault").queryData(".type");
	else if(dictype == "Dic_Department")
	    entitylist = view.id("dsSffault").queryData("[@.get('bumen')=='" + name + "'||@.get('relatebumen')=='" + name + "']");
	else if(dictype == "Dic_MajorSystem")
	    entitylist = view.id("dsSffault").queryData("[@.get('majorsystem')=='" + name + "'||@.get('relatemajor')=='" + name + "']");
	var count = 0;
    entitylist.each(function(entity){
		if(dictype == "Dic_DutyType" || dictype == "Dic_Gzfs"){
		    var has = entity.indexOf(name);
			if(has == -1)
			    return;
	    }
        count++;//JSON只有这样才能拿到个数
    });
    arg.value = count;
}

ToolBarButton.ClientEvent:
var ftype = view.id("formElement1").get("value");
view.id('dsSffault').getData().insert({
	ftype: ftype
});
var cur = view.id('dsSffault').getData("#")
if(cur.get("foundopp") == "飞行中"){
	view.id("DataGridSfstatistic").set("visible",true);
}else
{
	view.id("DataGridSfstatistic").set("visible",false);
}
view.id('diaSffault').show();
view.id("tabControl1").set("currentIndex",1);
var dsSfstatistic = this.get("#dsSfstatistic");
dsSfstatistic.set("parameter", {
	ftype: ftype
});
dsSfstatistic.flushAsync();	

	/*
 	 * 对用户新增操作进行记录，在用户操作日志表中新增一条记录。
     */
	var myDate = new Date();
	view.id('dsUserOperationLog').getData().insert({
		logOperationType:"0",
		operationPerson: "${loginUsername}",
		operationContent:"对问题追踪表新增一条记录",
		operationTime:myDate
	});
	view.id('uaUserOperationLog').execute();

if(view.id("tabControl1").get("currentIndex") == 1){
	var cur=view.id('dsSffault').getData('#');
	if (cur) {
		view.id('diaSffault').show();
	}
	var dsSfstatistic = this.get("#dsSfstatistic");
dsSfstatistic.set("parameter", {
	ftype:cur.get("ftype")
});
dsSfstatistic.flushAsync();	
	if(cur.get("foundopp") == "飞行中"){
		view.id("DataGridSfstatistic").set("visible",true);
	}else
	{
		view.id("DataGridSfstatistic").set("visible",false);
	}
}
else{
	dorado.MessageBox.alert("请先切换至“问题跟踪管理”页面！");
}

	/*
 	 * 对用户修改操作进行记录，在用户操作日志表中新增一条记录。
 	 */
	var myDate = new Date();
	view.id('dsUserOperationLog').getData().insert({
		logOperationType:"1",
		operationPerson: "${loginUsername}",
		operationContent:"对问题追踪表修改选定记录",
		operationTime:myDate
	});
	view.id('uaUserOperationLog').execute();

if(view.id("tabControl1").get("currentIndex") == 1){
    var cur=view.id('dsSffault').getData('#');
	var ua=view.id('uaSffault');
	if(cur){
		dorado.MessageBox.confirm('您是否确认删除此条记录?', function(){
			
			/*
 			 * 对用户删除操作进行记录，在用户操作日志表中新增一条记录。
 			 */
			var myDate = new Date();
			view.id('dsUserOperationLog').getData().insert({
				logOperationType:"2",
				operationPerson: "${loginUsername}",
				operationContent:"对问题追踪表删除所选记录",
				operationTime:myDate
			});
			view.id('uaUserOperationLog').execute();
			
			cur.remove();
			ua.execute();
				}); 
	} 
}
else{
	dorado.MessageBox.alert("请先切换至“问题跟踪管理”页面！");
}

var dsSffault = this.get("#dsSffault");
var ftype = view.id("formElement1").get("value");
dsSffault.set("parameter", {
	ftype: ftype
});
dsSffault.flushAsync();

DataColumn.ClientEvent:
jQuery(arg.dom).empty();
jQuery(arg.dom).xCreate({
	tagName:"A",
	href:"#",
	content:"查看",
	onclick:function(){
		view.id("tabControl1").set("currentIndex",1);
		var name = view.id("dsGzfs").getData("#").get("name");
		var ftype = view.id("formElement1").get("value");
		var dsSffault = view.id("dsSffault");
        dsSffault.set("parameter", {
	        ftype: ftype,
			gzfs:name == "总计"?null:name
        });
        dsSffault.flushAsync();
	}
});

