Button.ClientEvent:
var cur=view.id('dsSffault').getData('#');
var content = cur.get("gengxincontent");
if(content){
	var date = new Date();
	var year = date.getFullYear();
    var month = date.getMonth()+1;
    var day = date.getDate();
	var newgx = '更新日期:'+year+'-'+month+'-'+day+'\t\t更新人:${loginUser.getCname()}\t\t更新内容:'+content;
    var jl = cur.get("gengxinjl");
	if(jl)
	    newgx = jl + '\r\n' + newgx;	
    cur.set("gengxinjl",newgx);
	cur.set("gengxincontent",null);
}
view.id('uaSffault').execute(function(){
               view.id('diaSffault').hide();
           });

var cur=view.id('dsSffault').getData('#');
				cur.cancel();
				view.id('diaSffault').hide();
				
	/*
	 * 删除当前用户操作日志记录并保存
	 */
	view.id('dsUserOperationLog').getData().remove()
	view.id('uaUserOperationLog').execute();

