ToolBarButton.ClientEvent:
	var myDate = new Date();
    var mytime= myDate.toLocaleTimeString();
    var a=mytime.split(":");
    var hour=myDate.getHours();
    var tempId = hour*60*60*1000+a[1]*60*1000+a[2]*1000;//当前时间的毫秒数作为临时ID
    var ftype = view.id("formElement1").get("value");
	view.id('dsSfstatistic').getData().insert({ftype: ftype,id: tempId});
	view.id('diaSfstatistic').show();
	view.id("tabControl1").set("currentIndex",1);
	//view.id('dsTasklist').getData().insert();
	
	/*
 	 * 对用户新增操作进行记录，在用户操作日志表中新增一条记录。
     */
	var myDate = new Date();
	view.id('dsUserOperationLog').getData().insert({
		logOperationType:"0",
		operationPerson: "${loginUsername}",
		operationContent:"对飞行统计表新增一条记录",
		operationTime:myDate
	});
	view.id('uaUserOperationLog').execute();

if(view.id("tabControl1").get("currentIndex") == 1){
	var cur=view.id('dsSfstatistic').getData('#');
	if (cur) {
		view.id('diaSfstatistic').show();
		var taskno=view.id("autoformelement2").get("value");
		var dsTasklist = this.get("#dsTasklist");
		dsTasklist.set("parameter", {tasknumber: taskno});
		dsTasklist.flushAsync();
		//view.id("tabControl1").set("currentIndex",2);
	}
}
else{
	dorado.MessageBox.alert("请先切换至“试飞信息管理”页面！");
}

	/*
 	 * 对用户修改操作进行记录，在用户操作日志表中新增一条记录。
 	 */
	var myDate = new Date();
	view.id('dsUserOperationLog').getData().insert({
		logOperationType:"1",
		operationPerson: "${loginUsername}",
		operationContent:"对试飞日志表修改选定记录",
		operationTime:myDate
	});
	view.id('uaUserOperationLog').execute();

if(view.id("tabControl1").get("currentIndex") == 1){
var cur=view.id('dsSfstatistic').getData('#');
	var ua=view.id('uaSfstatistic');
	if(cur){
		dorado.MessageBox.confirm('您是否确认删除此条记录?', function(){
			
			/*
 			 * 对用户删除操作进行记录，在用户操作日志表中新增一条记录。
 			 */
			var myDate = new Date();
			view.id('dsUserOperationLog').getData().insert({
				logOperationType:"2",
				operationPerson: "${loginUsername}",
				operationContent:"对飞行统计表删除所选记录",
				operationTime:myDate
			});
			view.id('uaUserOperationLog').execute();
			
			cur.remove();
			ua.execute();
		}); 
	} 
}
else{
	dorado.MessageBox.alert("请先切换至“试飞信息管理”页面！");
}

