var aircrafttype=view.id("autoformelement1").get("value");
var taskno=view.id("autoformelement2").get("value");
var subject=view.id("formelement").get("value");
var taskcontent=view.id("autoformelement4").get("value");
var taskexecution=view.id("autoformelement5").get("value");
