    dorado.MessageBox.alert("着陆时间不能早于起飞时间！");
else
		{
			var cur1=view.id('dsTasklist').getData('#');

	cur1.set("aircrafttype",aircrafttype);
	cur1.set("tasknumber",taskno);
	cur1.set("subject",subject);
	cur1.set("taskcontent",taskcontent);
	cur1.set("taskexecution",taskexecution);
	view.id('uaSfstatistic').execute(function(){
         view.id('diaSfstatistic').hide();      
			    });
				
	var cur2 = this.get("#dsTasklist.data");
	cur2.each(function(Tasklist){
		if(Tasklist.get("tasknumber")==null){
			Tasklist.remove();
		}
});
	view.id('uaTasklist').execute();
	
	   
	   
	   }

var cur=view.id('dsSfstatistic').getData('#');
				cur.cancel();
				view.id('diaSfstatistic').hide();
				
	/*
	 * 删除当前用户操作日志记录并保存
	 */
	view.id('dsUserOperationLog').getData().remove()
	view.id('uaUserOperationLog').execute();

