/**
 * 处理下拉框多选复写，即对文本框的内容还原到科目树上。
 * 
 * @Author：Wang Rui
 * @version:1.0
 * @Data:2015.6.16
 */

//datatree获取选中行，迭代设置选中状态，清空选中
var nodes=view.get("#chooseDataTreeSubject").getCheckedNodes();
nodes.each(function(node){
	node.set("checked",false);
});


//编辑时将已有行设置为选中
var value = this.get("#formElement2.value");
if(value){
	function getAllNodes(nodes,stvalue){
		nodes.each(function(node){
			var children=node.get("nodes");
			if(children){
			children.each(function(node){ 
		if(node.get("data.name")==stvalue){
			selectedItems.push(node);	
		}
	});
			getAllNodes(children,stvalue);
			}
		});
	}
	var selectedItems=[];
	var p1=value.indexOf(",");
	if(p1==-1){              
		var dataList =view.get("#chooseDataTreeSubject").get("nodes");//顶层节点
		
		 dataList.each(function(node){ 
		if(node.get("data.name")==value){
			selectedItems.push(node);	
		}
	});
	getAllNodes(dataList,value);
	}
	else{

	while(p1!=-1){
	value1=value.substring(0,p1);
	var dataList =view.get("#chooseDataTreeSubject").get("nodes");
	
	dataList.each(function(entity){
		if(entity.get("data.name")==value1){
			selectedItems.push(entity);		
		}
	});
	getAllNodes(dataList,value1);
	value=value.substring(p1+1,value.length);
	var p1=value.indexOf(",");
	if(p1==-1){
		
	
	    dataList.each(function(entity){
		if(entity.get("data.name")==value){
			selectedItems.push(entity);	
		}
	});
	getAllNodes(dataList,value);
	}
	}
	
	}
	selectedItems.each(function(node){
	node.set("checked",true);
    });
	
}

ToolBarButton.ClientEvent:
var dataTree = this.get("#chooseDataTreeSubject");
var selectNodes = dataTree.getCheckedNodes();
var temp = [];//传入后台的数组
var time = 0;
var name = "";//传到文本框formElement2的字符串
var cur=this.get("#dsSfstatistic.data:#");
while(time < selectNodes.length){
	//以“，”分隔拼成字符串
	name += ","+selectNodes[time].get("data.name");
	//把每个选中的节点信息传入temp数组
	temp.push(selectNodes[time].get("data.name"));
	time = time + 1;
}
name=name+",";
//name = name.substring(0,name.length-1);//截取字符串，去掉末尾的逗号
cur.set("subject",name); 
//把数组传入后台即 parameter	
//关闭下拉框
view.id("querySubjectCustomDropDown").close();

/**
 * 1.在科目树下拉框中“取消”按钮的事件编辑。
 * 2.当点击“取消”时，树上节点全部不选。
 * 3.关闭下拉框。
 * 
 * @Author：Wang Rui
 * @version:1.0
 * @Data:2015.6.17
 */

var dataTree = this.get("#chooseDataTreeSubject");
function unselect(nodes) {
		nodes.each(function(node) {
			unselect(node.get("nodes"));
			node.set("checked", false);
		});
	}

unselect(dataTree.get("nodes"));
view.get("#querySubjectCustomDropDown").close();

