PropertyDef.ClientEvent:
var name = arg.entity.get("name");
var entitylist = [];
    entitylist = view.id("dsSfstatistic").queryData("[@.get('taskNo.subject')=='" + name + "']");
var count = 0;
entitylist.each(function(entity){
    count++;//JSON只有这样才能拿到个数
});
arg.value = count;

