UploadAction.ClientEvent:
var cur=view.id('dsSfstatistic').getData('#');
var value = "/Fly_Sfstatistic/" + cur.get("id") + "/";
self.set("multipartParams", {record: value});

var returnValue = arg.file.name;
var cur=view.id('dsSfstatistic').getData('#');
cur.set("filename1",returnValue);
var link = view.id("lefile1");
if(returnValue){
	var oid = cur.get("id");
    var href = "ServletDownload?filename=Fly_Sfstatistic/" + oid + "/" + returnValue;
    link.set({
		href: href,
		visible: true
	});
}
else{
    link.set("visible",false);
}

var error = arg.error;
dorado.MessageBox.show({
	title:error.message,
	message:error.file.name + " 超过大小限制:"+ self.get('maxFileSize'),
	icon: "WARNING",
	buttons: ["ok"]
});

