Button.ClientEvent:
var cur=view.id('dsSfstatistic').getData('#');
if(cur.get("timeqifei") > cur.get("timeland")){
	dorado.MessageBox.alert("着陆时间不能早于起飞时间！");
}else{
	view.id('uaSfstatistic').execute(function(){
         view.id('diaSfstatistic').hide();
		 });
		 }

var fileURL="upload/Fly_Tasklist/"+view.id('dsTasklist').getData("#").get("oid")+"/"+view.id('dsTasklist').getData("#").get("filename");
var url = encodeURI(fileURL);
var path="a.jsp?url='"+ url +"'";	
open(path,"_blank");

var tasklist = view.id("dsTasklist").getData("#");
var taskexecution = tasklist.get("taskexecution");
view.id("dsSfstatistic").getData("#").set("taskNo",tasklist.toJSON());
view.id("dsSfstatistic").getData("#").set("taskexecution",taskexecution);
view.id("dsSfstatistic").getData("#").reset("taskNo");
view.id("dgChooseTasklist").hide();

DataColumn.ClientEvent:
