});

ToolBarButton.ClientEvent:
var guid = "";
for(var i=1;i<=32;i++){
	var n = Math.floor(Math.random()*16.0).toString(16);
	guid += n;
	if((i==8)||(i==12)||(i==16)||(i==20))
	guid += "-";
	}
dialog = view.id('diaSfstatistic');
dialog.set("caption","新增飞行统计");
var ftype = view.id("formElement1").get("value");
if(ftype){
	view.id('dsSfstatistic').getData().insert({
		ftype: ftype,
		id: guid
		});	
	view.id('diaSfstatistic').show();
	view.id("tabControl1").set("currentIndex",1);
	var link1 = view.id("lefile1");
	link1.set("visible",false);
	var link2 = view.id("lefile2");
	link2.set("visible",false);
	var link3 = view.id("lefile3");
	link3.set("visible",false);
	var link4 = view.id("lefile4");
	link4.set("visible",false);
	}else{
		dorado.MessageBox.alert("请先选择机型！");
		}

if(view.id("tabControl1").get("currentIndex") == 1){
	var cur=view.id('dsSfstatistic').getData('#');
	if (cur) {
		view.id('diaSfstatistic').show();
	}
	var file1 = cur.get("filename1");
    var link1 = view.id("lefile1");
    if(file1){
        var oid = cur.get("id");
		var href = "ServletDownload?filename=Fly_Sfstatistic/" + oid + "/" + file1;
        link1.set({
	       href: href,
	       visible: true
        });
    }
    else
      link1.set("visible",false);
	var file2 = cur.get("filename2");
    var link2 = view.id("lefile2");
    if(file2){
        var oid = cur.get("id");
		var href = "ServletDownload?filename=Fly_Sfstatistic/" + oid + "/" + file2;
        link2.set({
	       href: href,
	       visible: true
        });
    }
    else
      link2.set("visible",false);
	var file3 = cur.get("filename3");
    var link3 = view.id("lefile3");
    if(file3){
        var oid = cur.get("id");
		var href = "ServletDownload?filename=Fly_Sfstatistic/" + oid + "/" + file3;
        link3.set({
	       href: href,
	       visible: true
        });
    }
    else
      link3.set("visible",false);
	var file4 = cur.get("filename4");
    var link4 = view.id("lefile4");
    if(file4){
        var oid = cur.get("id");
		var href = "ServletDownload?filename=Fly_Sfstatistic/" + oid + "/" + file4;
        link4.set({
	       href: href,
	       visible: true
        });
    }
    else
      link4.set("visible",false);
}
else{
	dorado.MessageBox.alert("请先切换至“试飞信息管理”页面！");
}

if(view.id("tabControl1").get("currentIndex") == 1){
var cur = view.id('dsSfstatistic').getData('#');
	var ua=view.id('uaSfstatistic');
	if(cur){
		dorado.MessageBox.confirm('您是否确认删除此条记录?', function(){
			cur.remove();
			ua.execute();
		}); 
	} 
}
else{
	dorado.MessageBox.alert("请先切换至“试飞信息管理”页面！");
}

if(view.id("tabControl1").get("currentIndex") == 1){
	var cur=view.id('dsSfstatistic').getData('#');
