DataColumn.ClientEvent:
jQuery(arg.dom).empty();
var file = arg.data.get("taskNo.tasknumber");
var oid = arg.data.get("taskNo.oid");
var name = arg.data.get("taskNo.filename");
jQuery(arg.dom).xCreate({
	tagName:"A",
	href:"#",
	content:arg.data.get("taskNo.tasknumber"),
	onclick: function(){   
        view.id("diaTasklist").show();
		var link = view.id("lefile");
		var href = "ServletDownload?filename=Fly_Tasklist/" + oid + "/" + name ;
    	link.set({
			href: href,
			visible: true
		});
    }
});
