View.ClientEvent:
self.buildOrder=function(nodes){
	var order=1;
	nodes.each(function(node){
		var entityOrder=node.get("data.orderno");
		order=entityOrder+1;
	});
	return order;
}
//orderno是给同一层级节点进行编号

ToolBarButton.ClientEvent:
var data=view.id("dataSetSubject").getData();
var newOrder=view.buildOrder(view.id("dataTreeSubject").get("root").get("nodes"));
var newEntity=data.insert({orderno:newOrder});//插入一条新的
view.id("dataTreeSubject").set("currentEntity",newEntity);
var cur=view.id("formelement").get("value");
var cur1=view.id("dataSetSubject").getData("!currentSubject");
cur1.set("ftype",cur);

var tree=view.id("dataTreeSubject");
var currentNode=tree.get("currentNode");
if(!currentNode){
	dorado.MessageBox.alert("请先选中一个父节点!");
	return;
}
var newOrder=view.buildOrder(currentNode.get("nodes"));
var currentEntity=currentNode.get("data");
var newEntity=currentEntity.createChild("children",{
	parentnode:currentEntity.get("name"),
	orderno:newOrder
});
currentNode.expand();
tree.set("currentEntity",newEntity);
var cur=view.id("formelement").get("value");
var cur1=view.id("dataSetSubject").getData("!currentSubject");
cur1.set("ftype",cur);

	var myDate = new Date();
	view.id('dsUserOperationLog').getData().insert({
		logOperationType:"0",
		operationPerson: "${loginUsername}",
		operationContent:"对试飞科目表新增一条子节点记录",
		operationTime:myDate
	});
	view.id('uaUserOperationLog').execute();

var tree=view.id("dataTreeSubject");
var entity=tree.get("currentEntity");
if(entity){
	view.id("ajaxActionCheckChildren").set("parameter", {
		parentnode:entity.get("name"),
		ftype:entity.get("ftype")
		}).execute(function(count){
		if (count>0){
			dorado.MessageBox.alert("请先删除子节点");
		} else {
			dorado.MessageBox.confirm("真的要删除当前节点？",function(){
			
				var myDate = new Date();
				view.id('dsUserOperationLog').getData().insert({
					logOperationType:"2",
					operationPerson: "${loginUsername}",
					operationContent:"对试飞科目表删除所选节点",
					operationTime:myDate
				});
				view.id('uaUserOperationLog').execute();
			
				entity.remove();
				var firstNode=tree.get("firstNode");
				if(firstNode){
					tree.set("currentNode",firstNode);
				}
				//view.id("updateActionSubject").execute();
			});
		}
	});
}else{
	dorado.MessageBox.alert("当前没有节点可供删除！");
}

DataTree.ClientEvent:
var node=arg.oldCurrent;//当前节点
if(node){
	var data = node.get("data");
	if(data && data.state!=dorado.Entity.STATE_NONE){
		view.id("updateActionSubject").execute();
		if(data.validate()!="ok"){
			arg.processDefault=false;
		}
	}
	
}

var draggingInfo=arg.draggingInfo;//拖拽信息
if(!draggingInfo){
	return true;
}
var parentNode=draggingInfo.get("targetObject");//targetObject，对象被拖拽到哪个对象
if(!parentNode){
	return true;
}
var parentId=parentNode.get("data.name");//父节点ID
var order=1;
parentNode.get("nodes").each(function(node){//遍历所有子节点
	var data=node.get("data");
	data.set("orderno",order++);
	data.set("parentnode",parentId);
});
view.id("updateActionSubject").execute();

view.id("menuOperate").show({
	position: {
		left: arg.event.pageX,
		top: arg.event.pageY
	}
});

var dataTree = this.get("#dataTreeSubject");
var node = dataTree.get("currentNode");
if(node){
	var name = node.get("data").get("name");
	if(name == "通用试飞科目"){
	view.set({
		"^DirtyNode.disabled": true
	});
}
else
view.set({
		"^DirtyNode.disabled": false
	});
	
}

