DataSetDropDown.ClientEvent:
var dataSetSubject = this.get("#dataSetSubject");
dataSetSubject.set("parameter",{
	ftype:arg.selectedValue
	});
dataSetSubject.flushAsync();

UpdateAction.ClientEvent:
var cur=view.id('dataSetSubject');
var ftype=view.id("formelement").get("value");
cur.set("parameter",{
	ftype:ftype
	});
cur.flushAsync();
var cur1=view.id('dsSubject');
cur1.set("parameter",{
	});
cur1.flushAsync();

DataSetDropDown.ClientEvent:
var dataSetSubject = this.get("#dataSetSubject");
dataSetSubject.set("parameter", {
	ftype: arg.selectedValue
});
dataSetSubject.flushAsync();
if(arg.selectedValue)
	view.set({
		"^Dirty.disabled" : false});
else
	view.set({
		"^Dirty.disabled" : true});
