View.ClientEvent:
window.openUrlInFrameTab=function(url,caption,icon,double){
	if(!url){
		dorado.MessageBox.alert("Url不能空");
		return;
	}
	if(!caption){
		dorado.MessageBox.alert("caption不能空");
		return;
	}
	if(!icon){
		icon="url(skin>common/icons.gif) -40px -120px";
	}
	var tabControl=view.id("tabControlWorkarea");
	var tab=tabControl.getTab(url);
	if(!tab){
		tab=new dorado.widget.tab.IFrameTab({
			name:url,
			path:url,
			caption:caption,
			icon:icon,
			closeable:true
		});
		tabControl.addTab(tab);
	}else if(double){
		tab.set("path",url);
		if(caption){
			tab.set("caption",caption);
		}
	}
	tabControl.set("currentTab",tab);
}

DataSet.ClientEvent:
String.prototype.replaceAll = function(reallyDo, replaceWith, ignoreCase) {
	if (!RegExp.prototype.isPrototypeOf(reallyDo)) {
	    return this.replace(new RegExp(reallyDo, (ignoreCase ? "gi": "g")), replaceWith);
	} else {
	    return this.replace(reallyDo, replaceWith);
	}
}
var expanded="${configure.getBoolean("bdf2.mainFrameMenuExpanded")}";
var accordionNav=view.id("accordionNav");
var listArray = [];
var treeJson={};
var data=self.getData();
if(!data){
	return;
}
data.each(function(url){
	var section=new dorado.widget.Section();
	section.set("caption",url.get("name"));
	if(url.get("desc")){
		section.set("tip",url.get("desc"));	
	}
	if(url.get("icon")){
		section.set("icon",url.get("icon"));	
	}
	section.set("name",url.get("id"));
	section.set("control",new dorado.widget.Tree());
	section.set("userData",url);
	accordionNav.addSection(section);
});

accordionNav.set("onCurrentSectionChange",function(accrodion,arg){
	var section=arg.newSection;
	var id=section.get("name");
	if(treeJson[id]){
		return;
	}
	treeJson[id]="tree";
	var url=section.get("userData");
	var cdata=url.get("children");
	if(!cdata){
		return;
	}
	var tree=section.get("control");
	tree.set("onClick",function(self,arg){
		var node=self.get("currentNode");
		var data=node.get("userData");
		if(data.get("url")){
			window.openUrlInFrameTab(data.get("url"),data.get("name"),data.get("icon"));
		}
	});
	tree.set("onDoubleClick",function(self,arg){
		var node=self.get("currentNode");
		var data=node.get("userData");
		if(data.get("url")){
			window.openUrlInFrameTab(data.get("url"),data.get("name"),data.get("icon"),true);
		}
	});		
	var rootNode=tree.get("root");
	cdata.each(function(d){
		buildTree(rootNode,d);
	});
});

function buildTree(topNode,url){
	var node=new dorado.widget.tree.Node();
	if(expanded=="true"){
		node.set("expanded",true);
	}
	node.set("label",url.get("name"));
	if(url.get("desc")){
		node.set("tip",url.get("desc"));
	}
	if(url.get("icon")){
		node.set("icon",url.get("icon"));
	}
	node.set("userData",url);
	topNode.addNode(node);
	var cdata=url.get("children");
	if(!cdata){
		return;
	}
	cdata.each(function(d){
		buildTree(node,d);
	});
}

MenuItem.ClientEvent:
setTimeout(function(){
	var panelWorkarea=view.id("panelWorkarea");
	var maximized=panelWorkarea.get("maximized");
	var maxIcon=">dorado/res/icons/max.png";
	var normalIcon=">dorado/res/icons/min.png";
	if(maximized){
		self.set("icon",maxIcon);
		self.set("caption","最大化");
		panelWorkarea.maximizeRestore();
	}else{
		self.set("icon",normalIcon);
		self.set("caption","还原");
		panelWorkarea.maximize();
	}
}, 600);
