AjaxAction.ClientEvent:
self.todoTaskCount=0;
var tip;
function doFetchTodoTaskCount(){
	var action=view.id("$ajaxActionFetchTodoTaskCount");
	if(action.get("disabled")){
		return;
	}
	action.execute(function(result){
			var personal=parseInt(result.personal);
			var group=parseInt(result.group);
			var info="";
			if(personal>0){
				info="有"+personal+"条待办任务,";
			}
			if(group>0){
				info+="您有"+group+"条可领取的待办任务,";
			}
			info+="点击查看";
			if(group>0 || personal>0){
				var url="${configure.getString("bdf2.uflo.listTodoTaskUrl")}";
				if(tip){
					tip.set("visible",false);					
				}else{
					tip=dorado.widget.NotifyTipManager.notify({
						text:info,
						caption:"待办任务提醒",
						autoHide:true,
						style:{cursor:"pointer"},
						onClick:function(){
							var cardBook = view.id("cardBook1");
                            var iFrame = view.id("iFrame1");
                            cardBook.set("currentIndex", 1);
	                        iFrame.set("path", "view.comm.d");
						}
					});
				}
				tip.show();
			}else{
				if(tip && tip.get("visible")){
					tip.set("visible",false);					
				}
			}
		});
}

self.fetchTodoTaskCount=function(){
	self.intervalHandler=setInterval(doFetchTodoTaskCount,parseInt("${configure.getString("bdf2.uflo.fetchInternalTodoTaskCountInterval")}"));
	self.set("onDestroy",function(){
		if(self.intervalHandler){
			window.clearInterval(self.intervalHandler);
		}
	});
};
self.fetchTodoTaskCount();
doFetchTodoTaskCount();
